\begin{document}$$\alpha\hbox{-}{\hbox{ketoglutarate}}({\hbox{a-KG}}) + {\hbox{NH}}^{ + }_{4} + {\hbox{NADPH}}{\mathop \Leftrightarrow \limits^{{\text{GDH}}} }{\hbox{Glutamate}}+{\hbox{NADP}},$$\end{document}2\documentclass[12pt]{minimal}
